import React, { useState } from 'react'

export const Clock = () => {
    const [time,setTime]=useState('22:16:23');
    setInterval(()=>{ setTime(new Date().toLocaleTimeString())
    },1000);
  return (
    <div style={{textAlign:'center',margin:'5.5rem 0'}}>
        <h1 >{time}</h1>
    </div>
  )
}
