import React, { useState } from 'react'
import { Msgbox } from './Msgbox';

export const Googlekeep = () => {
  const name='';
   const [show,setShow] = useState(false);
   const [item,setItem] =useState({
    tiAr:[],
    desAr:[]
   });
   const [msg,setMsg] =useState({
    timsg:'',
    desmsg:''
   });
   const showDes=()=>{
    setShow(true);
   }
   const add=()=>{
    setItem((tival,desval)=>{
      if(name==='titleName'){
        return [...tival,msg.timsg];}
        else
        {
          return [...desval,msg.desmsg];
        }
       // return [...oldVal,msg.desmsg,msg.timsg];
    })
       
   }
   const getMes=(event)=>{
   const {name,value}=event.target;
   setMsg(prevVal=>{
    return{
      ...prevVal,
      [name]:value,
    };
   });
   
   }
  return (
    <>
      <div className="card" style={{margin:'6rem 12rem 2rem 10rem',height:'11rem',width:'16rem',border:'1px solid black',background:'orange'}}>
        <div className="title" style={{margin:'2rem 1rem',background:'gray',height:'3rem',width:'12rem'}}>
            <input type="text"  placeholder='Write your title' style={{height:'1.7rem',borderRadius:'10px'}} onChange={getMes} name='titleName' value={msg.timsg}/>
            <button onClick={showDes}>âž•</button>
        </div>
       {
         show? <input type="text" placeholder='Write description' onChange={getMes} name='desName' value={msg.desmsg}/> :""
       }  
         <button onClick={add}>Add more</button>
        </div>  
        <div style={{display:'flex'}}>
      {/* {
        item.map((Val,indval)=>{
            return(
            <Msgbox key={indval} timsg={Val}/>
            )
        })
      } */}
      </div>
    </>
  )
}
