import React, { useState } from 'react'

export const Form = () => {
  const [name,setName]= useState({
    fname:'',
    lname:''
  });
  const changeInput=(event)=>{
    console.log(event.target.value);
    console.log(event.target.name);
    const val=event.target.value;
    const nam=event.target.name;
    setName((prevval)=>{
      //console.log(prevval.fname);
      if(name==='fname')
      {
        return{
        fname:value,
        lname:prevval.lname,
        };
      }
    })
  };
  const upddateVal=(e)=>{
   e.preventDefault(); 

  };
  return (
    <div style={{textAlign:'center',background:'purple',height:'100vh',width:'100vw'}}>
      <form onSubmit={upddateVal} >
        <h1>Hello {name.fname} {name.lname}</h1><br />
        <input 
        onChange={changeInput}
        value={name.fname}
        name='fname'
         />
         <br />
          <input 
        onChange={changeInput}
        value={name.lname}
        name='lname'
         />
         <br />
        <button type='submit'>Click me</button>
        </form>
    </div>
  )
}
