import React, { useContext, useState } from "react";
import "./Navbar.css";
import { assets } from "../assets/assets/assets.js";
import { Link } from "react-router-dom";
import Cart from "../../pages/cart/Cart.js";
import { StoreContext } from "../../context/StoreContext.js";
const Navbar = ({ setShowlogin }) => {
  const [menu, setMenu] = useState("");
  const {subTotal} =useContext(StoreContext)
  const list = (val) => {
    return setMenu(val);
  };
  const [dark,setDark]=useState("white");
    const darktheme=()=>{
      //  if(document.body.classList.contains("dark")){
      //       document.body.classList.remove("dark")
      //  }else{
      //   document.body.classList.add("dark")
 
      // }
           if(dark==="white")
           {
             document.body.classList.add("dark")
            setDark('dark')
           }
           else{
            //document.body.classList.remove("dark")
            setDark("white")
           }
    }
  return (

    <div className="navbar">
     
      <Link to="/">
        <img src={assets.logo} alt="food" className="logo" />
      </Link>

      <ul className="navmenu">
        <Link
          to="/"
          onClick={() => setMenu("home")}
          className={menu === "home" ? "active" : ""}
        > Home</Link>
        <a
          href="#exploer-menu"
          onClick={() => setMenu("menu")}
          className={menu === "menu" ? "active" : ""}
        >
          Menu
        </a>
        <a
          href="#app-download"
          onClick={() => setMenu("ma")}
          className={menu === "ma" ? "active" : ""}
        >
          mobile-app
        </a>
        <a
          href="#footer"
          onClick={() => setMenu("contact")}
          className={menu === "contact" ? "active" : ""}
        >
          Contact us
        </a>
      </ul>
      <div className="navbar-right">
        <img src={assets.search_icon} alt="" />
        <div className="navabar-search-icon">
         <Link to="/cart"><img src={assets.basket_icon} alt="" /></Link>
          <div className={subTotal()===0?"":"dot"}> </div>
        </div>
        <button onClick={() => setShowlogin(true)} on>
          Sign in
        </button>
      </div>
      <div className="btn">
     <input type="checkbox" class="checkbox" id="checkbox"/>
  <label for="checkbox" class="checkbox-label" onClick={darktheme} >
    <i className="fas fa-moon"></i>
    <i className="fas fa-sun"></i>
    <span className="ball"></span>
  </label>
     </div>
     
    </div>
  );
};

export { Navbar };
