import { createContext, useState } from "react";
import { food_list } from "../component/assets/assets/assets.js";
 const StoreContext=createContext(null);

const StoreContextProvider= (props)=>{

  const [cartItem,setCartItem] =useState({});
  const addTocart= (item_id)=>{
    if(!cartItem[item_id])
    {
        setCartItem((prev)=>({...prev,[item_id]:1}))
    }
    else{
        setCartItem(prev=>({...prev,[item_id]:prev[item_id]+1}))
    }
  }
  const removeTocart=(item_id)=>{
    setCartItem(prev=>({...prev,[item_id]:prev[item_id]-1}))
  }
    const contextVal={
     food_list,
     addTocart,
     removeTocart
    }
    return(
        <StoreContext.Provider value={contextVal}>
            {props.children}
        </StoreContext.Provider>
    )
}
export  {StoreContextProvider,StoreContext};
