import React, { useContext, useState } from 'react'
import './cart.css'
//import StoreCon from '../../context/StoreCon.js'
import StoreContextProvider from '../../context/StoreContextProvider.js';
const Cart = () => {
  const {cartItems,food_list,removeFromCart}=useContext(StoreContextProvider);
  return (
    <div className='cart'>
      <div className="cart-items">
        <div className="cart-items-title">
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <br />
        <hr />
        {
            food_list.map((item,ind)=>{
              if(cartItems[item._id]>0)
              {
                return (
                  <div className='cart-item-title item'>
                    <p>{item.name}</p>
                  </div>
                )
              }
            })
        }
      </div>
    </div>
  )
}

export default Cart
