import React, { useContext, useState } from "react";
import "./cart.css";
import { StoreContext } from "../../context/StoreContext.js";
//import { food_list } from '../../component/assets/assets/assets.js'

const Cart = () => {
  const { cartItem, food_list, removeToCart } = useContext(StoreContext);
  const add = (num) => {
    return num;
  };
  return (
    <div className="cart">
      <div className="cart-items">
        <div className="cart-items-title">
          <th>
            <td>Items</td>
            <td>Title</td>
            <td>Price</td>
            <td>Quantity</td>
            <td>Total</td>
            <td>Remove</td>
          </th>
        </div>
        <br />
        <hr />

        {food_list.map((val, ind) => {
          if (cartItem[ind] > 0) {
            return (
              <div className="showInfo">
                <img src={val.image} alt="" />
                <p>{val.name}</p>
                <p>{val.price}</p>
                <p>{cartItem[ind]}</p>
                <p>{val.price * cartItem[ind]}</p>
                <p>X</p>
              </div>
            );
          }
        })}
      </div>
    </div>
  );
};

export default Cart;
