import React, { useState } from 'react'

export const Form = () => {
  const [name,setName]= useState({
    fname:'',
    lname:''
  });
  
  const changeInput=(event)=>{
    const val=event.target.value;
    const nam=event.target.name;
    setName((prevval)=>{
      //console.log(prevval.fname);
      if(nam==='fname')
      {
        return{
        fname:val,
        lname:prevval.lname,
        };
      }
      else{
        return{
          fname:prevval.fname,
          lname:val,
          };
      }
    })
  };
  const upddateVal=(e)=>{
   e.preventDefault(); 
   
  };
  return (
    <div style={{textAlign:'center',background:'purple',height:'100vh',width:'100vw'}}>
      <form onSubmit={upddateVal} >
        <h1>Hello {name.fname} {name.lname}</h1><br />
        <input 
        onChange={changeInput}
        placeholder='Enter your first name'
        value={name.fname}
        name='fname'
        style={{height:'2.5rem', width:'12rem',background:'lightgray',fontSize:'1rem',borderRadius:'1.5rem',paddingLeft:'1rem'}}
         />
         <br /><br />
          <input 
        onChange={changeInput}
        placeholder='Enter your last name'
        value={name.lname}
        name='lname'
        style={{height:'2.5rem', width:'12rem',background:'lightgray',fontSize:'1rem',borderRadius:'1.5rem',paddingLeft:'1rem'}}
         />
         <br /><br />
         <input 
        onChange={changeInput}
        placeholder='E-mail'
        value={name.lname}
        name='lname'
        style={{height:'2.5rem', width:'12rem',background:'lightgray',fontSize:'1rem',borderRadius:'1.5rem',paddingLeft:'1rem'}}
         />
         <br /><br />
          <input 
        onChange={changeInput}
        placeholder='password'
        value={name.lname}
        name='lname'
        style={{height:'2.5rem', width:'12rem',background:'lightgray',fontSize:'1rem',borderRadius:'1.5rem',paddingLeft:'1rem'}}
         />
         <br /><br />
        <button type='submit' 
        style={{height:'2.2rem', width:'5.5rem', background:'green',fontSize:'1.1rem',color:'black',borderRadius:'1rem'}}
        >Click me</button>
        </form>
    </div>
  )
}
