export const Useref = () => {
   const demo= useRef(null);
   const [show,setShow] =useState(true);
  return (
    <div>
        <form action="" onSubmit={(e)=>{
           e.preventDefault();
           const name=demo.current.value;
        //    name===''?alert('Pls fill data'):setShow(true);
        }}>
            <div>
                <label htmlFor="demo">enter your lickyName</label>
                <input type="text" id='demo' ref={demo} />
            </div>
            <br />
            <button>Submit</button>
        </form>
        <p>{show? `your lucky name is ${demo.current.value}`: ""}</p>
        {/* <p>Your lucky name is {demo.current.value}</p> */}
    </div>
  )
}
