import React, { useContext, useState } from 'react'
import './cart.css'
import { StoreContext } from '../../context/StoreContext.js';
//import { food_list } from '../../component/assets/assets/assets.js'

const Cart = () => {
  const {cartItem,food_list,removeToCart}=useContext(StoreContext);
  const [add,setAdd]=useState(0);
  return (
    <div className='cart'>
      <div className="cart-items">
        <div className="cart-items-title">
        <th>
                <td>Items</td>
                <td>Title</td>
                <td>Price</td>
                <td>Quality</td>
                <td>Total</td>
                <td>Remove</td>
              </th>
        </div>
        <br />
        <hr />
        
           {
            food_list.map((val,ind)=>{
             if(cartItem[ind]>0)
             {
                 return(
                     <div className='showInfo'>
                         <p>{val.image}</p>
                         <p>{val.name}</p>
                         <p>{val.price}</p>
                         <p>{}</p>
                         <p>{setAdd((prev)=>prev+val.price)}</p>
                         <p>X</p>
                     </div>
                 )
             }
            })
        }
      </div>
    </div>
  )
}

export default Cart
