import React, { useContext, useState } from "react";
import "./cart.css";
import { StoreContext } from "../../context/StoreContext.js";

const Cart = () => {
  const { cartItem, food_list, removeToCart } = useContext(StoreContext);
  const removeClass = () => {
    const child=document.querySelector('.cart-items-title cart-items-item');
    return child.replaceChildren();
  };
  return (
    <div className="cart">
      <div className="cart-items">
        <div className="cart-items-title">
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <br />
        <hr />

        {food_list.map((val, ind) => {
          if (cartItem[ind] > 0) {
            return (
              <>
              <div className="cart-items-title cart-items-item">
                <img src={val.image} alt="" />
                <p>{val.name}</p>
                <p>{val.price}</p>
                <p>{cartItem[ind]}</p>
                <p>{val.price * cartItem[ind]}</p>
                <p className="cross" onClick={removeClass}>x</p>
              </div>
              <hr />
              </>
            );
          }
        })}
      </div>
    </div>
  );
};

export default Cart;
