import React, { useState } from 'react'

export const Todo = () => {
    const [todo,setTodo]=useState('');
    const change=()=>{
      {let div=document.querySelector('.card')
       div.appendChild(<div>
        <span>{todo}</span>
       </div>)
    }
    }
    const show=(e)=>{
      const value=e.target.value;
      setTodo(value);
    }
  return (
    <div className="main">
        <div className="card">
            <h1>TODO LIST</h1>
            <div className="write">
            <input type="text"
            onChange={show}
             value={value}
            />
            <button className='write'onClick={change}>+</button>
            </div>
            {/* <div className="list" style={{display:'flex'}}>
                <p>{todo}</p>
            </div> */}
        </div>
    </div>
  )
}
