// App.js
import React, { useState } from 'react';
import PDFViewer from './miniproject/PdfViewr';

const App = () => {
  const [files, setFiles] = useState([]);

  // Function to handle file selection
  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles(selectedFiles);
  };

  return (
    <div>
      {/* Input to select files */}
      <input type="file" onChange={handleFileChange} accept=".pdf" multiple />

      {/* Display links for each uploaded file */}
      {files.map((file, index) => (
        <div key={index}>
          <button onClick={() => window.open(URL.createObjectURL(file))}>
            {file.name}
          </button>
          <PDFViewer file={file} />
        </div>
      ))}
    </div>
  );
};

export default App;
