import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import styled from 'styled-components';
import { yupResolver } from '@hookform/resolvers/yup';

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 2rem;
  padding: 2rem;
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.1);
  @media screen and (max-width: 768px) {
    width: 90%;
  }
`;

const Label = styled.label`
  margin-bottom: 0.5rem;
  font-weight: bold;
  color: #333333;
`;

const Input = styled.input`
  padding: 0.5rem;
  margin-bottom: 1rem;
  border: 1px solid #cccccc;
  border-radius: 4px;
  width: 100%;
  max-width: 300px;
  color: #333333;
`;

const TextArea = styled.textarea`
  padding: 0.5rem;
  margin-bottom: 1rem;
  border: 1px solid #cccccc;
  border-radius: 4px;
  width: 100%;
  max-width: 300px;
  min-height: 150px;
  resize: vertical;
  color: #333333;
`;

const Button = styled.button`
  padding: 0.5rem 1rem;
  background-color: #4caf50;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;
  &:hover {
    background-color: #45a049;
  }
`;

const ErrorMessage = styled.p`
  margin-top: -0.5rem;
  margin-bottom: 1rem;
  color: #ff0000;
`;

const SuccessMessage = styled.p`
  margin-top: -0.5rem;
  margin-bottom: 1rem;
  color: #009933;
`;

const CharacterCount = styled.p`
  margin-top: -0.5rem;
  margin-bottom: 1rem;
  color: #666666;
`;

const AdditionalInfo = styled.div`
  margin-top: 2rem;
  text-align: center;
`;

const ContactPage = () => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [submitError, setSubmitError] = useState(null);
  const [characterCount, setCharacterCount] = useState(0);

  const { register, handleSubmit, reset, formState: { errors } } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = async (data) => {
    setIsSubmitting(true);
    try {
      // Simulate form submission (replace this with your actual submission code)
      await new Promise((resolve) => setTimeout(resolve, 1000));
      setIsSubmitted(true);
      reset();
    } catch (error) {
      setSubmitError(error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Label>Name:</Label>
        <Input type="text" {...register("name")} />
        {errors.name && <ErrorMessage>{errors.name.message}</ErrorMessage>}

        <Label>Email:</Label>
        <Input type="email" {...register("email")} />
        {errors.email && <ErrorMessage>{errors.email.message}</ErrorMessage>}

        <Label>Message:</Label>
        <TextArea {...register("message")} onChange={(e) => setCharacterCount(e.target.value.length)} />
        {errors.message && <ErrorMessage>{errors.message.message}</ErrorMessage>}
        <CharacterCount>{characterCount}/200 characters</CharacterCount>

        <Button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Submitting...' : 'Submit'}
        </Button>

        {isSubmitted && <SuccessMessage>Form submitted successfully!</SuccessMessage>}
        {submitError && <ErrorMessage>{submitError}</ErrorMessage>}

        <Button type="button" onClick={reset}>
          Reset
        </Button>
      </Form>
      <AdditionalInfo>
        <p>Contact us for any inquiries or support:</p>
        <p>Email: contact@example.com</p>
        <p>Phone: +1234567890</p>
        <p>Address: 123 Main Street, City, Country</p>
      </AdditionalInfo>
    </>
  );
};

export default ContactPage;
