import React, { useState } from 'react'

export const Myaccordian = ({question,answer}) => {
  const [show,setShow]= useState(false);
  return (
    <div>
      
        <div className="question" style={{display:'flex'}}>
         
         <h3>{question}</h3>
         <button onClick={()=>setShow(!show)}>{show?"➕":"➖"}</button>
        </div>
        {show && <p>{answer}</p>}
           
      
    </div>
  )
}
