import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import styled from 'styled-components';
import { yupResolver } from '@hookform/resolvers/yup';

const schema = yup.object().shape({
  name: yup.string().required('Name is required'),
  email: yup.string().email('Email is invalid').required('Email is required'),
  message: yup.string().required('Message is required').max(200, 'Message cannot exceed 200 characters'),
});

const FormContainer = styled.div`
  background-color: #f9f9f9;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  max-width: 600px;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const TextArea = styled.textarea`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const ErrorMessage = styled.p`
  color: #ff0000;
  margin-bottom: 10px;
`;

const SuccessMessage = styled.p`
  color: #009933;
  margin-bottom: 10px;
`;

function ContactPage() {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [submitError, setSubmitError] = useState(null);
  const [characterCount, setCharacterCount] = useState(0);

  const { register, handleSubmit, reset, formState: { errors } } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = async (data) => {
    setIsSubmitting(true);
    try {
      // Simulate form submission (replace this with your actual submission code)
      await new Promise((resolve) => setTimeout(resolve, 1000));
      setIsSubmitted(true);
      reset();
    } catch (error) {
      setSubmitError(error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <FormContainer>
      <h2>Contact Us</h2>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input type="text" placeholder="Your Name" {...register("name")} />
        {errors.name && <ErrorMessage>{errors.name.message}</ErrorMessage>}

        <Input type="email" placeholder="Your Email" {...register("email")} />
        {errors.email && <ErrorMessage>{errors.email.message}</ErrorMessage>}

        <TextArea placeholder="Your Message" {...register("message")} onChange={(e) => setCharacterCount(e.target.value.length)} />
        {errors.message && <ErrorMessage>{errors.message.message}</ErrorMessage>}
        <p>Characters remaining: {200 - characterCount}</p>

        <Button type="submit" disabled={isSubmitting}>{isSubmitting ? 'Submitting...' : 'Submit'}</Button>

        {isSubmitted && <SuccessMessage>Form submitted successfully!</SuccessMessage>}
        {submitError && <ErrorMessage>{submitError}</ErrorMessage>}
      </Form>
    </FormContainer>
  );
}

export default ContactPage;
