import React from 'react';
import { useForm } from 'react-hook-form';

function ContactForm() {
  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    console.log(data); // You can replace this with your form submission logic
  };

  return (
    <div>
      <h2>Contact Us</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" ref={(ref) => register(ref, { required: true })} />
          {errors && errors.name && <span>This field is required</span>}
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" ref={(ref) => register(ref, { required: true, pattern: /^\S+@\S+$/i })} />
          {errors && errors.email && <span>Please enter a valid email address</span>}
        </div>
        <div>
          <label>Message:</label>
          <textarea name="message" ref={(ref) => register(ref, { required: true })} />
          {errors && errors.message && <span>This field is required</span>}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default ContactForm;
