import React, { useContext, useState } from "react";
import "./cart.css";
import { StoreContext } from "../../context/StoreContext.js";

const Cart = () => {
  const { cartItem, food_list, removeToCart } = useContext(StoreContext);
  //const [remove, setRemove] = useState([true]);
  return (
    <div className="cart">
      <div className="cart-items">
        <div className="cart-items-title">
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <br />
        <hr />

        {food_list.map((val, ind) => {
          if (cartItem[ind] > 0 && remove) {
            return (
              <>
                <div className="cart-items-title cart-items-item">
                  <img src={val.image} alt="" />
                  <p>{val.name}</p>
                  <p>{val.price}</p>
                  <p>{cartItem[ind]}</p>
                  <p>{val.price * cartItem[ind]}</p>
                  <p
                    className="cross"
                    onClick={removeToCart(ind)}
                  >
                    x
                  </p>
                </div>
                <hr />
              </>
            );
          } else {
            return <></>;
          }
        })}
      </div>
    </div>
  );
};

export default Cart;
