import React, { useState } from 'react'
import { Msgbox } from './Msgbox';

export const Googlekeep = () => {
   const [show,setShow] = useState(false);
   const [item,setItem] =useState([]);
   const [msg,setMsg] =useState('');
   const showDes=()=>{
    setShow(true);
   }
   const add=()=>{
    setItem(oldVal=>{
        return [...oldVal,msg];
    })
   }
   const getMes=(event)=>{
   setMsg(event.target.value);
   }
  return (
    <>
      <div className="card" style={{margin:'6rem 12rem 2rem 10rem',height:'11rem',width:'16rem',border:'1px solid black',background:'orange'}}>
        <div className="title" style={{margin:'2rem 1rem',background:'gray',height:'3rem',width:'12rem'}}>
            <input type="text"  placeholder='Write your title' style={{height:'1.7rem',borderRadius:'10px'}} onChange={getMes} value={msg}/>
            <button onClick={showDes}>âž•</button>
        </div>
       {
         show? <input type="text" placeholder='Write description' /> :""
       }  
         <button onClick={add}>Add more</button>
        </div>  
      {
        item.map((ele)=>{
            // console.log(ele);
            <h2>{ele}</h2>
        })
      }
    </>
  )
}
